UPDATE "public"."personage" SET "name" = 'Selka', "description" = '[Le style qui me ressemble]', "description1" = 'Jeune apprentie nonne vivant à Rulid, le village tout au nord de l’Underworld. Elle a souffert d’être dans l’ombre de sa brilliante sœur Alice, mais les mots de Kirito lui viennent en alde lorsqu’il lui dit :« Tu as un talent bien à toi Selka ». À la demande de Kirito qui cherche à rejoindre la Capitale Centrale, elle lui raconte le mythe de la genèse de ce monde.', "attack1" = 'Umbra Ball C', "descriptionattack1" = 'Attaque magique sur un ennemi (faible). Charge la Volonté de 15,00%', "mpattack1" = '22', "attack2" = 'Recover Seal All', "descriptionattack2" = 'Soins HP sur tous les alliés (faible). Guérit des sceaux.', "mpattack2" = '21', "attack3" = 'Umbra Dome (Volonté)', "descriptionattack3" = 'Attaque magique de Volonté sur tous les ennemis.', "mpattack3" = '??', "hp" = '3331', "mp" = '211', "atk" = '1617', "matk" = '1703', "def" = '1020', "mdef" = '1026', "crit" = '503', "spd" = '281' WHERE "id" = '53';
