UPDATE "public"."personage" SET "name" = 'Renri', "description" = '[Le chevalier ailé]', "description1" = 'Renri Synthesis Twenty Seven, le 27e Chevalier de l’intégrité de l’église Axiom dans Underworld. Bien qu’il n’ait aucun souvenir, c’était un jeune épéiste prodige extraordinaire. Après le rituel de synthèse, il a perdu confiance lorsqu’il ne pouvait pas manier correctement l’objet divin: les ailes à double tranchant. Cependant, alors qu’il fuyait le champ de bataille, voir Kirito protéger Tiese et les autres le fit enfin revenir à ses esprits. Surmontant ses peurs, il est désormais capable d’utiliser tout le potentiel des Twin Edged Wings.', "attack1" = 'Umbra Ball C', "descriptionattack1" = 'Attaque magique sur un ennemi (faible). Charge la Volonté de 15,00%', "mpattack1" = '22', "attack2" = 'Recover Seal All', "descriptionattack2" = 'Soins HP sur tous les alliés (faible). Guérit des sceaux.', "mpattack2" = '21', "attack3" = 'Umbra Dome (Volonté)', "descriptionattack3" = 'Attaque magique de Volonté sur tous les ennemis.', "mpattack3" = '??', "hp" = '3331', "mp" = '211', "atk" = '1617', "matk" = '1703', "def" = '1020', "mdef" = '1026', "crit" = '503', "spd" = '281' WHERE "id" = '78';
UPDATE "public"."personage" SET "name" = 'Administrator', "description" = '[Un piège exquis]', "description1" = 'L’archevêque suprême de l’Égise de l’Axiome, Administrator, est étendue sur son lit à baldaquin au dernier étage de la cathédrale. « Si tu m’aimes, cet amour, je te le rendrai ! » disait-elle pour exiger un amour puissant. Elle souhaitait rester à jamais belle et jeune. Elle emprisonna toute personne s’opposant à l’ordre établi par la Table des Interdits qui tentait de changer le Monde des Hommes, et effaça la mémoire de ceux qu’elle aimait le plus en faisant d’eux ses sujets les plus loyaux, les Chevaliers de l’Intégrité.', "attack1" = 'Umbra Ball C', "descriptionattack1" = 'Attaque magique sur un ennemi (faible). Charge la Volonté de 15,00%', "mpattack1" = '22', "attack2" = 'Recover Seal All', "descriptionattack2" = 'Soins HP sur tous les alliés (faible). Guérit des sceaux.', "mpattack2" = '21', "attack3" = 'Umbra Dome (Volonté)', "descriptionattack3" = 'Attaque magique de Volonté sur tous les ennemis.', "mpattack3" = '??', "hp" = '3331', "mp" = '211', "atk" = '1617', "matk" = '1703', "def" = '1020', "mdef" = '1026', "crit" = '503', "spd" = '281' WHERE "id" = '79';

