UPDATE "public"."personage" SET "name" = 'Yui', "description" = '[La jeune fille du matin brumeux]', "description1" = 'Après avoir perdu la mémoire, Yui fut secourue par Kirito et Asuna alors qu’elle errait dans la forët, et commença à vivre avec eux comme une vraie famille. Cependant, elle fut mëlée à des problèmes en partant à la recherche de ses véritables parents dans la « ville du début », et entra dans le donjon, le « Château de fer noir ». Là, un boss, la faux fatale, attendait Kirito et ses amis et les plongea dans une situation critique avec un seul coup. Face à l’inévitable, et retrouvant la mémoire, Yui s’avance douvement vers l’ennemi.', "attack1" = 'Umbra Ball C', "descriptionattack1" = 'Attaque magique sur un ennemi (faible). Charge la Volonté de 15,00%', "mpattack1" = '22', "attack2" = 'Recover Seal All', "descriptionattack2" = 'Soins HP sur tous les alliés (faible). Guérit des sceaux.', "mpattack2" = '21', "attack3" = 'Umbra Dome (Volonté)', "descriptionattack3" = 'Attaque magique de Volonté sur tous les ennemis.', "mpattack3" = '??', "hp" = '3331', "mp" = '211', "atk" = '1617', "matk" = '1703', "def" = '1020', "mdef" = '1026', "crit" = '503', "spd" = '281' WHERE "id" = '96';
